WHT=$(tput setaf 7); RED=$(tput setaf 1); GRN=$(tput setaf 2); bold=$(tput bold); und=$(tput smul); normal=$(tput sgr0); FTemp=$(vcgencmd measure_temp |tr -cd '[[:digit:]].'); echo "${bold}Temperature:${normal}"; echo "Celsius: ${FTemp}"; echo "Kelvin: $(echo "${FTemp} + 273" | bc)"; echo "Fahrenheit: $(echo "1.8*${FTemp}+32" | bc)"; stmsg="${und}Status:${normal}"; if [ ${FTemp:0:1} -gt 58 ]; then echo "${stmsg} ${RED}Too hot!"; else echo "${stmsg} ${GRN}Safe"; fi;
